package JavaExercise;

public class swapTwoNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
    int x,y,t;
    x=2;
    y=3;
    t = x;
    x= y;
    y=t;
     
     System.out.println("Value of x:"+x);
     System.out.println("Value of y:"+y);
	}

}
